import React, { useState } from 'react';
import axios from 'axios';

const SignupForm = () => {
    const [formData, setFormData] = useState({ username: '', email: '', password: '', roles: [] });
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;

        if (name === 'roles') {
            const roles = [...formData.roles];
            if (e.target.checked) {
                roles.push(value);
            } else {
                const index = roles.indexOf(value);
                roles.splice(index, 1);
            }
            setFormData({ ...formData, roles });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate form fields
        if (!formData.username || !formData.email || !formData.password || formData.roles.length === 0) {
            setError('Error: Username, email, password, and at least one role are required.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:8080/api/auth/signup', formData);
            console.log(response.data);
            setError('');
        } catch (err) {
            setError('Error signing up: ' + err.response.data.message);
        }
    };

    return (
        <div>
            <h2>Sign Up</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input type="text" name="username" value={formData.username} onChange={handleChange} />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={formData.email} onChange={handleChange} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" name="password" value={formData.password} onChange={handleChange} />
                </div>
                <div>
                    <label>Roles:</label>
                    <div>
                        <label>
                            <input type="checkbox" name="roles" value="user" onChange={handleChange} /> User
                        </label>
                    </div>
                    <div>
                        <label>
                            <input type="checkbox" name="roles" value="moderator" onChange={handleChange} /> Moderator
                        </label>
                    </div>
                    <div>
                        <label>
                            <input type="checkbox" name="roles" value="admin" onChange={handleChange} /> Admin
                        </label>
                    </div>
                </div>
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
};

export default SignupForm;
