// AddEmployee.js
import React, { useState } from 'react';
import axios from 'axios';
import './AddEmployee.css'; // Import the CSS file for AddEmployee styling

const AddEmployee = ({ fetchEmployees }) => {
  const [newEmployee, setNewEmployee] = useState({
    firstName: '',
    lastName: '',
    designation: '',
    salary: 0
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewEmployee({ ...newEmployee, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8080/api/employees', newEmployee);
      setNewEmployee({
        firstName: '',
        lastName: '',
        designation: '',
        salary: 0
      });
      fetchEmployees();
    } catch (error) {
      console.error('Error creating employee:', error);
    }
  };

  return (
    <div className="add-employee-container"> {/* Apply container styling */}
      <h3 className="add-employee-heading">Add Employee</h3> {/* Apply heading styling */}
      <form onSubmit={handleSubmit}>
        <input type="text" name="firstName" placeholder="First Name" value={newEmployee.firstName} onChange={handleInputChange} className="add-employee-input" required /> {/* Apply input field styling */}
        <input type="text" name="lastName" placeholder="Last Name" value={newEmployee.lastName} onChange={handleInputChange} className="add-employee-input" required /> {/* Apply input field styling */}
        <input type="text" name="designation" placeholder="Designation" value={newEmployee.designation} onChange={handleInputChange} className="add-employee-input" required /> {/* Apply input field styling */}
        <input type="number" name="salary" placeholder="Salary" value={newEmployee.salary} onChange={handleInputChange} className="add-employee-input" required /> {/* Apply input field styling */}
        <button type="submit" className="add-employee-button">Add Employee</button> {/* Apply button styling */}
      </form>
    </div>
  );
};

export default AddEmployee;
