import React, { useState } from 'react';
import axios from 'axios';

const SignupForm = () => {
    const [formData, setFormData] = useState({ username: '', email: '', password: '', roles: ['user'] }); // Default role is 'user'
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate form fields
        if (!formData.username || !formData.email || !formData.password) {
            setError('Error: Username, email, and password are required.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:8080/api/auth/signup', formData);
            console.log(response.data);
            setError(''); // Reset error state upon successful registration
        } catch (err) {
            setError('Error signing up: ' + err.response.data.message);
        }
    };

    return (
        <div>
            <h2>Sign Up</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input type="text" name="username" value={formData.username} onChange={handleChange} />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={formData.email} onChange={handleChange} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" name="password" value={formData.password} onChange={handleChange} />
                </div>
                {/* Optionally include role input */}
                {/* <div>
                    <label>Roles:</label>
                    <input type="text" name="roles" value={formData.roles} onChange={handleChange} />
                </div> */}
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
};

export default SignupForm;
