import React, { useState } from 'react';
import axios from 'axios';
import '../styles/UpdateDeleteEmployee.css';

const UpdateDeleteEmployee = ({ employee, fetchEmployees }) => {
  const [updatedEmployee, setUpdatedEmployee] = useState({ ...employee });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedEmployee({ ...updatedEmployee, [name]: value });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:8080/api/employees/${updatedEmployee.id}`, updatedEmployee);
      fetchEmployees();
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:8080/api/employees/${updatedEmployee.id}`);
      fetchEmployees();
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  return (
    <div className="update-delete-employee-container">
      <h3 className="update-delete-employee-heading">Update Employee</h3>
      <input type="text" name="fullName" placeholder="Full Name" value={updatedEmployee.fullName} onChange={handleInputChange} className="update-delete-employee-input" required />
      <input type="text" name="designation" placeholder="Designation" value={updatedEmployee.designation} onChange={handleInputChange} className="update-delete-employee-input" required />
      <input type="number" name="salary" placeholder="Salary" value={updatedEmployee.salary} onChange={handleInputChange} className="update-delete-employee-input" required />
      <button onClick={handleUpdate} className="update-delete-employee-button">Update Employee</button>
      <button onClick={handleDelete} className="update-delete-employee-button">Delete Employee</button>
    </div>
  );
};

export default UpdateDeleteEmployee;
