// ViewEmployees.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/ViewEmployees.css';

const ViewEmployees = ({ handleUpdateEmployee, handleDeleteEmployee }) => {
  const [employees, setEmployees] = useState([]);
  const [error, setError] = useState('');
  const [editMode, setEditMode] = useState({});

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      const response = await axios.get('http://localhost:8080/api/employees', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      const formattedEmployees = response.data.map(employee => {
        const [firstName, lastName] = employee.fullName.split(' ');
        return {
          ...employee,
          firstName,
          lastName
        };
      });

      setEmployees(formattedEmployees);
      setError('');
    } catch (error) {
      console.error('Error fetching employees:', error);
      setError('Failed to fetch employees. Please try again later.');
    }
  };

  const handleUpdate = async (id, updatedEmployeeData) => {
    try {
      const token = localStorage.getItem('accessToken');
      await axios.put(`http://localhost:8080/api/employees/${id}`, updatedEmployeeData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      fetchEmployees(); // Fetch updated list of employees after updating
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('accessToken');
      await axios.delete(`http://localhost:8080/api/employees/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setEmployees(employees.filter(employee => employee.id !== id));
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  const toggleEditMode = (id) => {
    setEditMode(prevState => ({
      ...prevState,
      [id]: !prevState[id] // Toggle edit mode for the specific employee ID
    }));
  };

  return (
    <div className="view-employees-container">
      <h2 className="view-employees-heading">Employees</h2>
      {error && <p className="view-employees-error">{error}</p>}
      <table className="employee-table">
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Designation</th>
            <th>Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(employee => (
            <tr key={employee.id} className="employee-row">
              <td>{editMode[employee.id] ? <input type="text" defaultValue={employee.firstName} id={`firstName${employee.id}`} /> : employee.firstName}</td>
              <td>{editMode[employee.id] ? <input type="text" defaultValue={employee.lastName} id={`lastName${employee.id}`} /> : employee.lastName}</td>
              <td>{editMode[employee.id] ? <input type="text" defaultValue={employee.designation} id={`designation${employee.id}`} /> : employee.designation}</td>
              <td>{editMode[employee.id] ? <input type="text" defaultValue={employee.salary} id={`salary${employee.id}`} /> : employee.salary}</td>
              <td>
                {editMode[employee.id] ? (
                  <>
                    <button onClick={() => handleUpdate(employee.id, { 
                      firstName: document.querySelector(`#firstName${employee.id}`).value, 
                      lastName: document.querySelector(`#lastName${employee.id}`).value, 
                      designation: document.querySelector(`#designation${employee.id}`).value, 
                      salary: document.querySelector(`#salary${employee.id}`).value 
                    })}>Save</button>
                    <button onClick={() => toggleEditMode(employee.id)}>Cancel</button>
                  </>
                ) : (
                  <>
                    <button onClick={() => toggleEditMode(employee.id)}>Edit</button>
                    <button onClick={() => handleDelete(employee.id)}>Delete</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewEmployees;
