import React, { useState } from 'react';
import axios from 'axios';
import '../styles/UpdateDeleteEmployee.css';

const UpdateDeleteEmployee = ({ employee, fetchEmployees }) => {
  const [updatedEmployee, setUpdatedEmployee] = useState({ ...employee });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedEmployee({ ...updatedEmployee, [name]: value });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`http://localhost:8080/api/employees/${updatedEmployee.id}`, updatedEmployee);
      fetchEmployees(); // Refresh the employee list after update
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:8080/api/employees/${updatedEmployee.id}`);
      fetchEmployees(); // Refresh the employee list after delete
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  return (
    <div className="update-delete-employee-container">
      <h3 className="update-delete-employee-heading">Update Employee</h3>
      <p>Full Name: {employee.fullName}</p>
      <p>Designation: {employee.designation}</p>
      <p>Salary: {employee.salary}</p>
      <input type="text" name="designation" placeholder="New Designation" value={updatedEmployee.designation} onChange={handleInputChange} className="update-delete-employee-input" required />
      <input type="number" name="salary" placeholder="New Salary" value={updatedEmployee.salary} onChange={handleInputChange} className="update-delete-employee-input" required />
      <button onClick={handleUpdate} className="update-delete-employee-button">Update</button>
      <button onClick={handleDelete} className="update-delete-employee-button">Delete</button>
    </div>
  );
};

export default UpdateDeleteEmployee;
