import React, { useState } from 'react';
import axios from 'axios';
import LoginForm from '../components/LoginForm';
import Signup from '../components/Signup';
import Navbar from '../components/Navbar';
import AddEmployee from '../EmployeeCrud/AddEmployee';
import ViewEmployees from '../EmployeeCrud/ViewEmployees';
import UpdateDeleteEmployee from '../EmployeeCrud/UpdateDeleteEmployee';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';

const HomePage = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [showSignupForm, setShowSignupForm] = useState(false);
  const [showAddEmployee, setShowAddEmployee] = useState(false);
  const [showViewEmployees, setShowViewEmployees] = useState(false);
  const [showUpdateEmployee, setShowUpdateEmployee] = useState(false);
  const [showAlert, setShowAlert] = useState(false); // New state for alert message
  const [employees, setEmployees] = useState([]); // State to hold employees data
  const navigate = useNavigate();

  const handleLogout = () => {
    setLoggedIn(false);
    setUserRole(null);
    navigate('/login');
  };

  const handleShowLoginForm = () => {
    setShowLoginForm(true);
    setShowSignupForm(false);
  };

  const handleShowSignupForm = () => {
    setShowLoginForm(false);
    setShowSignupForm(true);
  };

  const handleViewEmployees = () => {
    setShowViewEmployees(true);
    setShowAddEmployee(false);
    setShowUpdateEmployee(false);
  };

  const handleAddEmployee = () => {
    setShowAddEmployee(true);
    setShowViewEmployees(false);
    setShowUpdateEmployee(false);
  };

  const handleUpdateEmployee = (employee) => {
    // Implement update logic
    console.log('Update employee:', employee);
  };

  const handleDeleteEmployee = (employee) => {
    // Implement delete logic
    console.log('Delete employee:', employee);
  };

  const handleAlertClose = () => {
    setShowAlert(false);
  };

  // Function to fetch employees data
  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      const response = await axios.get('http://localhost:8080/api/employees', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      // Handle error
    }
  };

  return (
    <div className="home-page-container">
      <Navbar loggedIn={loggedIn} onLogout={handleLogout} onShowLoginForm={handleShowLoginForm} onShowSignupForm={handleShowSignupForm} />
      {loggedIn && (
        <div className="logged-in-message">
          <h2>Welcome back, {userRole === 'ROLE_MODERATOR' ? 'ROLE_MODERATOR!!' : userRole}!</h2>
        </div>
      )}
      {!loggedIn ? (
        <div>
          {showLoginForm && <LoginForm setUserRole={setUserRole} setLoggedIn={setLoggedIn} />}
          {showSignupForm && <Signup />}
          {!showLoginForm && !showSignupForm && (
            <div className="welcome-message">
              <h2>Welcome to the Home Page!</h2>
              <p className="feature-message">Please login or signup to access the features.</p>
            </div>
          )}
        </div>
      ) : (
        <div>
          <div className="button-box">
            <button onClick={handleViewEmployees}>View Employees</button>
            {userRole === 'ROLE_ADMIN' && <button onClick={handleAddEmployee}>Add Employee</button>}
            {(userRole === 'ROLE_ADMIN' || userRole === 'ROLE_MODERATOR') && <button onClick={handleUpdateEmployee}>Update Employee</button>}
          </div>
          {showViewEmployees && <ViewEmployees employees={employees} handleUpdateEmployee={handleUpdateEmployee} handleDeleteEmployee={handleDeleteEmployee} userRole={userRole} />}
          {showAddEmployee && <AddEmployee showAlert={setShowAlert} />} {/* Pass setShowAlert to AddEmployee component */}
          {showUpdateEmployee && <UpdateDeleteEmployee />}
          {showAlert && ( // Render the alert message if showAlert is true
            <div className="alert-message">
              <p>Employee created successfully!</p>
              <button onClick={handleAlertClose}>Close</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default HomePage;
