import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SignupForm = () => {
    const [formData, setFormData] = useState({ username: '', email: '', password: '', roles: [] }); // Initialize roles as an empty array
    const [error, setError] = useState('');
    const [availableRoles, setAvailableRoles] = useState([]); // State to store available roles

    useEffect(() => {
        // Fetch available roles from the backend
        fetchAvailableRoles();
    }, []);

    const fetchAvailableRoles = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/auth/roles');
            setAvailableRoles(response.data.roles); // Assuming response contains available roles array
        } catch (error) {
            console.error('Error fetching roles:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'roles') {
            // If roles are checkboxes, handle them separately
            const roles = [...formData.roles];
            if (e.target.checked) {
                roles.push(value);
            } else {
                const index = roles.indexOf(value);
                if (index !== -1) {
                    roles.splice(index, 1);
                }
            }
            setFormData({ ...formData, roles });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate form fields
        if (!formData.username || !formData.email || !formData.password) {
            setError('Error: Username, email, and password are required.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:8080/api/auth/signup', formData);
            console.log(response.data);
            setError(''); // Reset error state upon successful registration
        } catch (err) {
            setError('Error signing up: ' + err.response.data.message);
        }
    };

    return (
        <div>
            <h2>Sign Up</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input type="text" name="username" value={formData.username} onChange={handleChange} />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={formData.email} onChange={handleChange} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" name="password" value={formData.password} onChange={handleChange} />
                </div>
                <div>
                    <label>Select Role(s):</label>
                    {availableRoles.map(role => (
                        <div key={role.name}>
                            <input
                                type="checkbox"
                                name="roles"
                                value={role.name}
                                onChange={handleChange}
                            />
                            <label>{role.name}</label>
                        </div>
                    ))}
                </div>
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
};

export default SignupForm;
