// ViewEmployees.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/ViewEmployees.css';

const ViewEmployees = () => {
  const [employees, setEmployees] = useState([]);
  const [error, setError] = useState('');
  const [editedEmployee, setEditedEmployee] = useState(null);

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      const response = await axios.get('http://localhost:8080/api/employees', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setEmployees(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching employees:', error);
      setError('Failed to fetch employees. Please try again later.');
    }
  };

  const handleEditEmployee = (employee) => {
    setEditedEmployee({ ...employee });
  };

  const handleSaveEmployee = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      await axios.put(`http://localhost:8080/api/employees/${editedEmployee.id}`, editedEmployee, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setEditedEmployee(null);
      fetchEmployees(); // Refresh the employee list after saving changes
    } catch (error) {
      console.error('Error saving employee:', error);
      setError('Failed to save employee. Please try again.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedEmployee({ ...editedEmployee, [name]: value });
  };

  return (
    <div className="view-employees-container">
      <h2 className="view-employees-heading">Employees</h2>
      {error && <p className="view-employees-error">{error}</p>}
      <table className="employee-table">
        <thead>
          <tr>
            <th>Full Name</th>
            <th>Designation</th>
            <th>Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(employee => (
            <tr key={employee.id} className="employee-row">
              <td>{editedEmployee && editedEmployee.id === employee.id ? <input type="text" name="fullName" value={editedEmployee.fullName} onChange={handleInputChange} /> : employee.fullName}</td>
              <td>{editedEmployee && editedEmployee.id === employee.id ? <input type="text" name="designation" value={editedEmployee.designation} onChange={handleInputChange} /> : employee.designation}</td>
              <td>{editedEmployee && editedEmployee.id === employee.id ? <input type="text" name="salary" value={editedEmployee.salary} onChange={handleInputChange} /> : employee.salary}</td>
              <td>
                {editedEmployee && editedEmployee.id === employee.id ? (
                  <>
                    <button onClick={() => handleSaveEmployee()}>Save</button>
                    <button onClick={() => setEditedEmployee(null)}>Cancel</button>
                  </>
                ) : (
                  <button onClick={() => handleEditEmployee(employee)}>Edit</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewEmployees;
