// HomePage.js
import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import AddEmployee from '../EmployeeCrud/AddEmployee';
import ViewEmployees from '../EmployeeCrud/ViewEmployees';
import UpdateDeleteEmployee from '../EmployeeCrud/UpdateDeleteEmployee';
import { useNavigate } from 'react-router-dom';

const HomePage = ({ loggedIn, userRole, onLogout }) => {
  const [showAddEmployee, setShowAddEmployee] = useState(false);
  const [showViewEmployees, setShowViewEmployees] = useState(false);
  const [showUpdateDeleteEmployee, setShowUpdateDeleteEmployee] = useState(false);
  const navigate = useNavigate();

  const handleShowComponent = (componentName) => {
    if (componentName === 'addEmployee') {
      setShowAddEmployee(true);
      setShowViewEmployees(false);
      setShowUpdateDeleteEmployee(false);
    } else if (componentName === 'viewEmployees') {
      setShowAddEmployee(false);
      setShowViewEmployees(true);
      setShowUpdateDeleteEmployee(false);
    } else if (componentName === 'updateDeleteEmployee') {
      setShowAddEmployee(false);
      setShowViewEmployees(false);
      setShowUpdateDeleteEmployee(true);
    }
  };

  return (
    <div>
      <Navbar loggedIn={loggedIn} onLogout={onLogout} onShowComponent={handleShowComponent} />
      <div>
        <h2>Home</h2>
        {loggedIn && (
          <div>
            <button onClick={() => handleShowComponent('addEmployee')}>Add Employee</button>
            <button onClick={() => handleShowComponent('viewEmployees')}>View Employees</button>
            {(userRole === 'admin' || userRole === 'moderator') && <button onClick={() => handleShowComponent('updateDeleteEmployee')}>Update/Delete Employee</button>}
          </div>
        )}
      </div>
      {showAddEmployee && <AddEmployee />}
      {showViewEmployees && <ViewEmployees />}
      {showUpdateDeleteEmployee && <UpdateDeleteEmployee />}
    </div>
  );
};

export default HomePage;
