import React, { useState, useEffect } from 'react';
import axios from 'axios';

const SignupForm = () => {
    const [formData, setFormData] = useState({ username: '', email: '', password: '', roles: ['user'] }); // Default role is 'user'
    const [error, setError] = useState('');
    const [availableRoles, setAvailableRoles] = useState([]); // State to store available roles

    useEffect(() => {
        // Fetch available roles when the component mounts
        fetchRoles();
    }, []);

    const fetchRoles = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/auth/roles');
            setAvailableRoles(response.data.roles); // Assuming response contains roles array
        } catch (error) {
            console.error('Error fetching roles:', error);
        }
    };

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate form fields
        if (!formData.username || !formData.email || !formData.password) {
            setError('Error: Username, email, and password are required.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:8080/api/auth/signup', formData);
            console.log(response.data);
            setError('');
        } catch (err) {
            setError('Error signing up: ' + err.response.data.message);
        }
    };

    return (
        <div>
            <h2>Sign Up</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input type="text" name="username" value={formData.username} onChange={handleChange} />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={formData.email} onChange={handleChange} />
                </div>
                <div>
                    <label>Password:</label>
                    <input type="password" name="password" value={formData.password} onChange={handleChange} />
                </div>
                <div>
                    <label>Roles:</label>
                    <select name="roles" multiple value={formData.roles} onChange={handleChange}>
                        {availableRoles.map(role => (
                            <option key={role} value={role}>{role}</option>
                        ))}
                    </select>
                </div>
                <button type="submit">Sign Up</button>
            </form>
        </div>
    );
};

export default SignupForm;
