// ViewEmployees.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/ViewEmployees.css';

const ViewEmployees = ({ handleUpdateEmployee, handleDeleteEmployee }) => {
  const [employees, setEmployees] = useState([]);
  const [error, setError] = useState('');
  const [updatedEmployeeData, setUpdatedEmployeeData] = useState({
    id: '',
    firstName: '',
    lastName: '',
    designation: '',
    salary: ''
  });

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      const response = await axios.get('http://localhost:8080/api/employees', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setEmployees(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching employees:', error);
      setError('Failed to fetch employees. Please try again later.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedEmployeeData({ ...updatedEmployeeData, [name]: value });
  };

  const handleUpdate = async (id) => {
    try {
      const token = localStorage.getItem('accessToken');
      await axios.put(`http://localhost:8080/api/employees/${id}`, updatedEmployeeData, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      fetchEmployees();
      // Clear the input fields after updating
      setUpdatedEmployeeData({
        id: '',
        firstName: '',
        lastName: '',
        designation: '',
        salary: ''
      });
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem('accessToken');
      await axios.delete(`http://localhost:8080/api/employees/${id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setEmployees(employees.filter(employee => employee.id !== id));
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  return (
    <div className="view-employees-container">
      <h2 className="view-employees-heading">Employees</h2>
      {error && <p className="view-employees-error">{error}</p>}
      <table className="employee-table">
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Designation</th>
            <th>Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(employee => (
            <tr key={employee.id} className="employee-row">
              <td>{employee.firstName}</td>
              <td>{employee.lastName}</td>
              <td>{employee.designation}</td>
              <td>{employee.salary}</td>
              <td>
                <input type="text" name="firstName" placeholder="New First Name" value={updatedEmployeeData.firstName} onChange={handleInputChange} />
                <input type="text" name="lastName" placeholder="New Last Name" value={updatedEmployeeData.lastName} onChange={handleInputChange} />
                <input type="text" name="designation" placeholder="New Designation" value={updatedEmployeeData.designation} onChange={handleInputChange} />
                <input type="number" name="salary" placeholder="New Salary" value={updatedEmployeeData.salary} onChange={handleInputChange} />
                <button onClick={() => handleUpdate(employee.id)}>Update</button>
                <button onClick={() => handleDelete(employee.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewEmployees;
