import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/ViewEmployees.css';

const ViewEmployees = () => {
  const [employees, setEmployees] = useState([]);
  const [error, setError] = useState('');
  const [userRole, setUserRole] = useState('');

  useEffect(() => {
    fetchEmployees();
    getUserRole(); // Fetch user role when component mounts
  }, []);

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      const response = await axios.get('http://localhost:8080/api/employees', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      console.log('Fetched employees:', response.data); // Debug output
      setEmployees(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching employees:', error);
      setError('Failed to fetch employees. Please try again later.');
    }
  };

  const getUserRole = async () => {
    try {
      // Fetch user role from the server
      const token = localStorage.getItem('accessToken');
      const response = await axios.get('http://localhost:8080/api/userRole', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setUserRole(response.data.role);
    } catch (error) {
      console.error('Error fetching user role:', error);
    }
  };

  const handleUpdateEmployee = (employeeId) => {
    // Logic to navigate to update employee page with employeeId
  };

  const handleDeleteEmployee = async (employeeId) => {
    try {
      const token = localStorage.getItem('accessToken');
      await axios.delete(`http://localhost:8080/api/employees/${employeeId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      fetchEmployees(); // Fetch updated employees after deletion
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  return (
    <div className="view-employees-container">
      <h2 className="view-employees-heading">Employees</h2>
      {error && <p className="view-employees-error">{error}</p>}
      <table className="employee-table">
        <thead>
          <tr>
            <th>Full Name</th>
            <th>Designation</th>
            <th>Salary</th>
            {userRole === 'ROLE_ADMIN' && <th>Actions</th>} {/* Render Actions column only for admins */}
          </tr>
        </thead>
        <tbody>
          {employees.map(employee => (
            <tr key={employee.id} className="employee-row">
              <td>{employee.fullName}</td>
              <td>{employee.designation}</td>
              <td>{employee.salary}</td>
              {userRole === 'ROLE_ADMIN' && ( // Render buttons only for admins
                <td>
                  <button onClick={() => handleUpdateEmployee(employee.id)}>Update</button>
                  <button onClick={() => handleDeleteEmployee(employee.id)}>Delete</button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewEmployees;
