// ViewEmployees.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/ViewEmployees.css';

const ViewEmployees = ({ handleUpdateEmployee, handleDeleteEmployee }) => {
  const [employees, setEmployees] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('accessToken');
      const response = await axios.get('http://localhost:8080/api/employees', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      // Split fullName into firstName and lastName
      const formattedEmployees = response.data.map(employee => {
        const [firstName, lastName] = employee.fullName.split(' ');
        return {
          ...employee,
          firstName,
          lastName
        };
      });
      setEmployees(formattedEmployees);
      setError('');
    } catch (error) {
      console.error('Error fetching employees:', error);
      setError('Failed to fetch employees. Please try again later.');
    }
  };

  return (
    <div className="view-employees-container">
      <h2 className="view-employees-heading">Employees</h2>
      {error && <p className="view-employees-error">{error}</p>}
      <table className="employee-table">
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Designation</th>
            <th>Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(employee => (
            <tr key={employee.id} className="employee-row">
              <td>{employee.firstName}</td>
              <td>{employee.lastName}</td>
              <td>{employee.designation}</td>
              <td>{employee.salary}</td>
              <td>
                <button onClick={() => handleUpdateEmployee(employee)}>Update</button>
                <button onClick={() => handleDeleteEmployee(employee)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewEmployees;
