// components/HomePage.js
import React, { useState } from 'react';
import LoginForm from '../components/LoginForm';
import Signup from '../components/Signup';
import Navbar from '../main/Navbar'; // Import the Navbar component
import AddEmployee from '../EmployeeCrud/AddEmployee';
import ViewEmployees from '../EmployeeCrud/ViewEmployees';
import UpdateDeleteEmployee from '../EmployeeCrud/UpdateDeleteEmployee';

const HomePage = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);

  // Function to handle login and set user role
  const handleLogin = (role) => {
    setLoggedIn(true);
    setUserRole(role);
  };

  // Function to handle logout
  const handleLogout = () => {
    setLoggedIn(false);
    setUserRole(null);
  };

  return (
    <div>
      <Navbar loggedIn={loggedIn} onLogout={handleLogout} /> {/* Pass loggedIn state and logout handler to Navbar */}
      {loggedIn ? ( // Conditionally render components based on login status
        <div>
          {userRole === 'admin' && <AddEmployee />} {/* Render AddEmployee component if user is admin */}
          <ViewEmployees /> {/* Always render ViewEmployees component */}
          {(userRole === 'admin' || userRole === 'moderator') && <UpdateDeleteEmployee />} {/* Render UpdateDeleteEmployee if user is admin or moderator */}
        </div>
      ) : (
        <div>
          <LoginForm onLogin={handleLogin} /> {/* Render login form if user is not logged in */}
          <Signup /> {/* Render signup form */}
        </div>
      )}
    </div>
  );
};

export default HomePage;
