import React, { useState } from 'react';
import LoginForm from '../components/LoginForm';
import Signup from '../components/Signup';
import Navbar from '../components/Navbar';
import AddEmployee from '../EmployeeCrud/AddEmployee';
import ViewEmployees from '../EmployeeCrud/ViewEmployees';
import UpdateDeleteEmployee from '../EmployeeCrud/UpdateDeleteEmployee';
import { useNavigate } from 'react-router-dom';
import '../styles/HomePage.css';

const HomePage = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [showSignupForm, setShowSignupForm] = useState(false);
  const navigate = useNavigate();

  const handleLogin = (role) => {
    setLoggedIn(true);
    setUserRole(role);
    setShowLoginForm(false);
    setShowSignupForm(false);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserRole(null);
    navigate('/login');
  };

  const handleShowLoginForm = () => {
    setShowLoginForm(true);
    setShowSignupForm(false);
  };

  const handleShowSignupForm = () => {
    setShowLoginForm(false);
    setShowSignupForm(true);
  };

  return (
    <div className="home-page-container">
      <Navbar loggedIn={loggedIn} onLogout={handleLogout} onShowLoginForm={handleShowLoginForm} onShowSignupForm={handleShowSignupForm} />
      {loggedIn ? (
        <div>
          {userRole === 'ROLE_ADMIN' && <AddEmployee />}
          {userRole !== 'ROLE_ADMIN' && <p>You don't have access to add employees.</p>}
          <ViewEmployees />
          {(userRole === 'ROLE_ADMIN' || userRole === 'ROLE_MODERATOR') && <UpdateDeleteEmployee />}
          {(userRole !== 'ROLE_ADMIN' && userRole !== 'ROLE_MODERATOR') && <p>You don't have access to update or delete employees.</p>}
        </div>
      ) : (
        <div>
          {showLoginForm && <LoginForm setUserRole={setUserRole} setLoggedIn={setLoggedIn} />}
          {showSignupForm && <Signup />}
          {!showLoginForm && !showSignupForm && (
            <div className="welcome-message">
              <h2>Welcome to the Home Page!</h2>
              <p className="feature-message">Please login or signup to access the features.</p>
            </div>
          )}
        </div>
      )}
      {loggedIn && (
        <div className="logged-in-message">
          <h2>Welcome back, {userRole}!</h2>
        </div>
      )}
    </div>
  );
};

export default HomePage;
