// HomePage.js
import React, { useState } from 'react';
import LoginForm from '../components/LoginForm';
import Signup from '../components/Signup';
import Navbar from '../components/Navbar';
import AddEmployee from '../EmployeeCrud/AddEmployee';
import ViewEmployees from '../EmployeeCrud/ViewEmployees';
import UpdateDeleteEmployee from '../EmployeeCrud/UpdateDeleteEmployee';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [showSignupForm, setShowSignupForm] = useState(false);
  const navigate = useNavigate();

  const handleLogin = (role) => {
    setLoggedIn(true);
    setUserRole(role);
    setShowLoginForm(false); // Hide login form after successful login
    setShowSignupForm(false); // Hide signup form after successful login
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserRole(null);
    // Redirect to login page after logout
    navigate('/login');
  };

  const handleShowLoginForm = () => {
    setShowLoginForm(true);
    setShowSignupForm(false);
  };

  const handleShowSignupForm = () => {
    setShowLoginForm(false);
    setShowSignupForm(true);
  };

  return (
    <div>
      <Navbar loggedIn={loggedIn} onLogout={handleLogout} onShowLoginForm={handleShowLoginForm} onShowSignupForm={handleShowSignupForm} />
      {loggedIn ? (
        <div>
          {userRole === 'admin' && <AddEmployee />}
          <ViewEmployees />
          {(userRole === 'admin' || userRole === 'moderator') && <UpdateDeleteEmployee />}
        </div>
      ) : (
        <div>
          {showLoginForm && <LoginForm setUserRole={setUserRole} setLoggedIn={setLoggedIn} />} {/* Pass setUserRole and setLoggedIn functions as props */}
          {showSignupForm && <Signup />}
          {!showLoginForm && !showSignupForm && (
            <div>
              <h2>Welcome to the Home Page!</h2>
              <p>Please login or signup to access the features.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default HomePage;
