import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/ViewEmployees.css';

const ViewEmployees = ({ fetchEmployees }) => {
  const [employees, setEmployees] = useState([]);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleUpdateEmployee = () => {
    // Logic to handle update for selectedEmployee
  };

  const handleDeleteEmployee = async () => {
    try {
      if (!selectedEmployee) {
        console.error('No employee selected.');
        return;
      }
      const token = localStorage.getItem('accessToken');
      await axios.delete(`http://localhost:8080/api/employees/${selectedEmployee.id}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      fetchEmployees(); // Fetch updated employees after deletion
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  return (
    <div className="view-employees-container">
      <h2 className="view-employees-heading">Employees</h2>
      {error && <p className="view-employees-error">{error}</p>}
      <table className="employee-table">
        <thead>
          <tr>
            <th>Full Name</th>
            <th>Designation</th>
            <th>Salary</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(employee => (
            <tr key={employee.id} className={selectedEmployee && selectedEmployee.id === employee.id ? 'selected' : ''}>
              <td>{employee.fullName}</td>
              <td>{employee.designation}</td>
              <td>{employee.salary}</td>
              <td>
                <button onClick={() => setSelectedEmployee(employee)}>Select</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedEmployee && (
        <div className="selected-employee-details">
          <h3>Selected Employee Details</h3>
          <p>Full Name: {selectedEmployee.fullName}</p>
          <p>Designation: {selectedEmployee.designation}</p>
          <p>Salary: {selectedEmployee.salary}</p>
          <button onClick={handleUpdateEmployee}>Update Employee</button>
          <button onClick={handleDeleteEmployee}>Delete Employee</button>
        </div>
      )}
    </div>
  );
};

export default ViewEmployees;
