import React, { useState } from 'react';
import axios from 'axios';

const LoginForm = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loginSuccess, setLoginSuccess] = useState(false); // State for tracking login success

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/auth/signin', { username, password });
      console.log('Login successful:', response.data);
      setLoginSuccess(true); // Set loginSuccess to true after successful login
      onLogin(response.data.role); // Pass the user role to the parent component
      // Handle successful login (e.g., redirect to dashboard)
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setError('Invalid username or password');
      } else {
        setError('An error occurred while logging in');
      }
      console.error('Login error:', error);
      // Handle login error (e.g., display error message)
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {!loginSuccess && ( // Render the form only if loginSuccess is false
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
          <div></div>
          <div></div>
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          <div></div>
          <div></div>
          <div></div>
          <button type="submit">Login</button>
        </form>
      )}
      {loginSuccess && <div className="success-message">Login successful!</div>}
      {error && <div className="error-message">{error}</div>}
    </div>
  );
};

export default LoginForm;
