// ViewEmployees.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import '../styles/ViewEmployees.css'; // Import the CSS file for ViewEmployees styling

const ViewEmployees = () => {
  const [employees, setEmployees] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      const token = localStorage.getItem('accessToken'); // Retrieve the JWT token from local storage
      const response = await axios.get('http://localhost:8080/api/employees', {
        headers: {
          'Authorization': `Bearer ${token}` // Include the JWT token in the Authorization header
        }
      });
      setEmployees(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching employees:', error);
      setError('Failed to fetch employees. Please try again later.');
    }
  };

  return (
    <div className="view-employees-container"> {/* Apply container styling */}
      <h2 className="view-employees-heading">Employees</h2> {/* Apply heading styling */}
      {error && <p className="view-employees-error">{error}</p>} {/* Apply error message styling */}
      <ul className="employee-list"> {/* Apply employee list styling */}
        {employees.map(employee => (
          <li key={employee.id} className="employee-item"> {/* Apply employee item styling */}
            <span className="employee-details">{employee.fullName} - {employee.designation} - {employee.salary}</span> {/* Apply employee details styling */}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ViewEmployees;
