import React, { useState } from 'react';
import axios from 'axios';

const Signup = () => {
    const [user, setUser] = useState({ username: '', email: '', password: '', roles: [] });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;

        // For roles, convert the selected options into an array
        const selectedRoles = Array.from(e.target.selectedOptions, (option) => option.value);

        setUser({ ...user, [name]: name === 'roles' ? selectedRoles : value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8080/api/auth/signup', user);
            setMessage(response.data.message);
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    return (
        <div className="container">
    <h2>Signup</h2>
    <form onSubmit={handleSubmit}>
        <div className="form-group">
            <label htmlFor="username">Username</label>
            <input type="text" id="username" name="username" placeholder="Enter username" value={user.username} onChange={handleChange} required />
        </div>
        <div className="form-group">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" placeholder="Enter email" value={user.email} onChange={handleChange} required />
        </div>
        <div className="form-group">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" name="password" placeholder="Enter password" value={user.password} onChange={handleChange} required />
        </div>
        <div className="form-group">
            <label htmlFor="roles">Select Role(s)</label>
            <select id="roles" name="roles" value={user.roles} onChange={handleChange} multiple>
                <option value="ROLE_USER">User</option>
                <option value="ROLE_MODERATOR">Moderator</option>
                <option value="ROLE_ADMIN">Admin</option>
            </select>
        </div>
        <button type="submit">Signup</button>
    </form>
    {message && <p>{message}</p>}
</div>

    );
};

export default Signup;
