import React, { useState } from 'react';
import LoginForm from '../components/LoginForm';
import Signup from '../components/Signup';
import ViewEmployees from '../EmployeeCrud/ViewEmployees';
import AddEmployee from '../EmployeeCrud/AddEmployee';
import UpdateDeleteEmployee from '../EmployeeCrud/UpdateDeleteEmployee';
import LogoutButton from '../components/LogoutButton';

const MenuBar = ({ loggedIn, onLogout }) => {
  return (
    <div className="menu-bar">
      {!loggedIn && (
        <ul>
          <li>Login</li>
          <li>Signup</li>
        </ul>
      )}
      {loggedIn && (
        <ul>
          <li>View Employees</li>
          <li>Logout</li>
        </ul>
      )}
    </div>
  );
};

const HomePage = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);

  const handleLogin = (role) => {
    setLoggedIn(true);
    setUserRole(role);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserRole(null);
  };

  return (
    <div>
      <h1>Welcome to the Employee Management System</h1>
      <MenuBar loggedIn={loggedIn} onLogout={handleLogout} />

      {/* Render login form and signup if not logged in */}
      {!loggedIn && (
        <div>
          <LoginForm onLogin={handleLogin} />
          <Signup />
        </div>
      )}

      {/* Render employee management components if logged in */}
      {loggedIn && (
        <div>
          {/* Conditionally render AddEmployee based on user role */}
          {userRole === 'admin' && <AddEmployee />}
          
          {/* Always render ViewEmployees */}
          <ViewEmployees />
          
          {/* Conditionally render UpdateDeleteEmployee based on user role */}
          {(userRole === 'admin' || userRole === 'moderator') && <UpdateDeleteEmployee />}
          
          {/* Logout button */}
          <LogoutButton onLogout={handleLogout} />
        </div>
      )}
    </div>
  );
};

export default HomePage;
