import React, { useState } from 'react';
import LoginForm from '../components/LoginForm';
import Signup from '../components/Signup';
import Navbar from '../components/Navbar';
import AddEmployee from '../EmployeeCrud/AddEmployee';
import ViewEmployees from '../EmployeeCrud/ViewEmployees';
import UpdateDeleteEmployee from '../EmployeeCrud/UpdateDeleteEmployee';

const HomePage = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userRole, setUserRole] = useState(null);
  const [showLoginForm, setShowLoginForm] = useState(false); // State to control login form visibility

  const handleLogin = (role) => {
    setLoggedIn(true);
    setUserRole(role);
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserRole(null);
  };

  const toggleLoginForm = () => {
    setShowLoginForm(!showLoginForm);
  };

  return (
    <div>
      <Navbar loggedIn={loggedIn} onLogout={handleLogout} />
      {loggedIn ? (
        <div>
          {userRole === 'admin' && <AddEmployee />}
          <ViewEmployees />
          {(userRole === 'admin' || userRole === 'moderator') && <UpdateDeleteEmployee />}
        </div>
      ) : (
        <div>
          {showLoginForm && <LoginForm onLogin={handleLogin} />}
          {!showLoginForm && <button onClick={toggleLoginForm}>Login</button>}
          <Signup />
        </div>
      )}
    </div>
  );
};

export default HomePage;
