// UpdateDeleteEmployee.js
import React, { useState } from 'react';
import axios from 'axios';

const UpdateDeleteEmployee = ({ employee, fetchEmployees }) => {
  const [updatedEmployee, setUpdatedEmployee] = useState({ ...employee });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedEmployee({ ...updatedEmployee, [name]: value });
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`/api/employees/${employee.id}`, updatedEmployee);
      fetchEmployees();
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/employees/${employee.id}`);
      fetchEmployees();
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  return (
    <div>
      <h3>Update Employee</h3>
      <input type="text" name="firstName" placeholder="First Name" value={updatedEmployee.firstName} onChange={handleInputChange} required />
      <input type="text" name="lastName" placeholder="Last Name" value={updatedEmployee.lastName} onChange={handleInputChange} required />
      <input type="text" name="designation" placeholder="Designation" value={updatedEmployee.designation} onChange={handleInputChange} required />
      <input type="number" name="salary" placeholder="Salary" value={updatedEmployee.salary} onChange={handleInputChange} required />
      <button onClick={handleUpdate}>Update Employee</button>
      <button onClick={handleDelete}>Delete Employee</button>
    </div>
  );
};

export default UpdateDeleteEmployee;
