import React, { useState } from 'react';
import './App.css';
import LoginForm from './components/LoginForm';
import Signup from './components/Signup';
import ViewEmployees from './components/ViewEmployees';
import AddEmployee from './components/AddEmployee';
import UpdateDeleteEmployee from './components/UpdateDeleteEmployee';
import LogoutButton from './components/LogoutButton';

const App = () => {
  const [token, setToken] = useState(null);

  const handleSetToken = (newToken) => {
    setToken(newToken);
    // Set token to localStorage or session storage
  };

  return (
    <div className="App">
      <h1>Welcome to the Employee Management System</h1>
      {token ? (
        <>
          <LogoutButton setToken={setToken} />
          <AddEmployee fetchEmployees={() => {}} />
          <ViewEmployees />
          {/* Additional components or routes for employee management */}
        </>
      ) : (
        <>
          <LoginForm setToken={handleSetToken} />
          <Signup />
        </>
      )}
    </div>
  );
};

export default App;
